mapper.insert:
        drop procedure if exists insert_while;
#         delimiter //
        create procedure insert_while()
        begin
            declare i int default 1;
            while i<101
            do
                insert into ${username}(id,state1,state2,state3) values(i,'flase','flase','flase');
                set i=i+1;
            end while;
            commit;
        end;
#         delimiter ;
        call insert_while();
